&amp; (y == y_offset || y == y_offset + size2 - 1)) || (isInRange(y, y_offset, size2) &amp;&amp; (x == x_offset || x == x_offset + size2 - 1))) { printf(&#34;*&#34;); } else if ((x &lt; size1 &amp;&amp; (y == 0 || y == size1 - 1)) || (y &lt; size1 &amp;&amp; (x == 0 || x == size1 - 1))) { printf(&#34;#&#34;); } else { printf(&#34; &#34;); } } printf(&#34;\n&#34;); } } int main() { squares(5, 2, 3, 4); return 0; } the other way to write code
